import React, { useState } from "react";
import styled from "styled-components";
import TextInput from "./TextInput";
import Button from "./Button";
import { UserSignUp } from "../api";
import { useDispatch } from "react-redux";
import { loginSuccess } from "../redux/reducers/userSlice";
// import { openSnackbar } from "../redux/reducers/snackbarSlice";

const Container = styled.div`
  width: 100%;
  max-width: 500px;
  display: flex;
  flex-direction: column;
  gap: 36px;
`;

const Title = styled.div`
  font-size: 30px;
  font-weight: 800;
  color: ${({ theme }) => theme.secondary};
`;
const Span = styled.div`
  font-size: 16px;
  font-weight: 400;
  color: ${({ theme }) => theme.secondary + 90};
`;

const Signup = () => {
  const validateInputs = () => {
    if (!email || !password) {
      alert("Please fill in all fields")
      return false
    }
    return true
  }
  const handleSignUp = async () => {
    setButtonLoading(true)
    setButtonDisabled(true)

    if (validateInputs) {
      await UserSignIn({ email, password }).then((res) => {
        dispatch(loginSuccess(res.data))
        setOpenAuth(false)
      }).catch((err) => {
        alert(err.response.data.message)
      }).finally(() => {
        setButtonLoading(false)
        setButtonDisabled(false)
      })
    }
  }
  return (
    <Container>
      <div>
        <Title>Welcome to Airbnb</Title>
        <span>Please login with your details here</span>
      </div>
      <div style={{ display: "flex", gap: "20px", flexDirection: "column" }}>
        <TextInput label="Email Address" placeholder="Enter your email address" />
        <TextInput label="Full Name" placeholder="Enter your full name" />
        <TextInput label="Password" placeholder="Enter your password" />
        <Button text="Sign Up" />
      </div>
    </Container>
  )
};

export default Signup;
